    private void jButtonEQUALActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonEQUALActionPerformed
    switch (deistEnum) {
           case PLUSС:{jLabelPOLEVVODA.setText("" + (oper1 + Integer.parseInt(jLabelPOLEVVODA.getText()))); break;}
           case MINUSС:{jLabelPOLEVVODA.setText("" + (oper1 - Integer.parseInt(jLabelPOLEVVODA.getText()))); break;}
           case MULTIPLYC:{jLabelPOLEVVODA.setText("" + (oper1 * Integer.parseInt(jLabelPOLEVVODA.getText()))); break;}
           case DIVEC:{jLabelPOLEVVODA.setText("" + (oper1 / Double.parseDouble(jLabelPOLEVVODA.getText()))); break;}
           default: {System.out.println("Что-то пошло не так");}
       }        
    }//GEN-LAST:event_jButtonEQUALActionPerformed

