    public void display (String number) {
         if (jLabelPOLEVVODA.getText()== "0") {
           jLabelPOLEVVODA.setText(number);
    }
         else {
             jLabelPOLEVVODA.setText(jLabelPOLEVVODA.getText() + number);
         }
    }
    private int oper1=0; //first operand
     private int deist=0; //ariphmetic deistvie 1=summ 2=substraction 3=multi 4= devide 0=nothing
     Deist deistEnum=Deist.NONE;
     
     private enum Deist {
         NONE, PLUSС, MINUSС, MULTIPLYC, DIVEC;
     }
    
    
